package Ifcondition;

public class Forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       for ( char c1='a'; c1<='z';c1++) {
    	   System.out.print(c1+"");
       }
       System.out.println(".........");
       
       for(char C1='A'; C1<='Z';C1++) {
    	   System.out.print(C1+"");
       }
       System.out.println(".........");
       for (char c1='z'; c1>='a';c1--) {
    	  System.out.print(c1+"") ; 
       }
	}

}
