package Logicalloop;

public class Whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int i = 0;
       System.out.println(" printing numbers from 1 to 5");
         while( i < 5) {
        	 System.out.println("Hello"+i);
        	 i++;
         }
	}

}
